import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { ArrowLeft, Award, Target, Trophy, Star, FileText, Calendar, TrendingUp, Users, Zap, Shield } from 'lucide-react';
import Scorecard from '../components/Scorecard';
import { getPlayer } from '../services/api';

const PlayerDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [player, setPlayer] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedMatch, setSelectedMatch] = useState(null);
  const [scorecardOpen, setScorecardOpen] = useState(false);

  const openScorecard = (match) => {
    setSelectedMatch(match);
    setScorecardOpen(true);
  };

  const closeScorecard = () => {
    setScorecardOpen(false);
    setTimeout(() => setSelectedMatch(null), 300);
  };

  const fetchPlayer = async () => {
    try {
      const response = await getPlayer(id);
      if (response.success) {
        setPlayer(response.data);
      }
    } catch (error) {
      console.error('Error fetching player:', error);
    } finally {
      setLoading(false);
    }
  };

  // Sample recent matches data - this would come from backend
  const getRecentMatches = () => {
    return [
      {
        _id: '1',
        matchNumber: 1,
        opponent: 'Mumbai Indians',
        venue: 'M. A. Chidambaram Stadium',
        date: '2025-03-22',
        status: 'Completed',
        scores: {
          csk: { runs: 178, wickets: 4, overs: 20 },
          opponent: { runs: 165, wickets: 8, overs: 20 }
        },
        result: { winner: 'CSK', summary: 'CSK won by 13 runs' },
        playerPerformance: {
          runs: 45,
          balls: 32,
          fours: 4,
          sixes: 2,
          wickets: 0,
          economy: 0
        },
        // Add sample batting and bowling data for scorecard
        cskBatting: [
          { name: 'Ruturaj Gaikwad', runs: 45, balls: 32, fours: 4, sixes: 2, strikeRate: 140.6, out: 'c & b Bumrah' },
          { name: 'Devon Conway', runs: 52, balls: 38, fours: 5, sixes: 1, strikeRate: 136.8, out: 'bowled Bumrah' },
          { name: 'MS Dhoni', runs: 28, balls: 20, fours: 2, sixes: 2, strikeRate: 140.0, out: 'run out' },
          { name: 'Shivam Dube', runs: 35, balls: 22, fours: 3, sixes: 2, strikeRate: 159.1, out: 'c & b Chahar' }
        ],
        opponentBatting: [
          { name: 'Rohit Sharma', runs: 42, balls: 35, fours: 4, sixes: 1, strikeRate: 120.0, out: 'c Dhoni b Jadeja' },
          { name: 'Ishan Kishan', runs: 38, balls: 28, fours: 3, sixes: 2, strikeRate: 135.7, out: 'c Gaikwad b Dube' },
          { name: 'Suryakumar Yadav', runs: 25, balls: 18, fours: 2, sixes: 1, strikeRate: 138.9, out: 'c Dhoni b Jadeja' }
        ],
        cskBowling: [
          { name: 'Ravindra Jadeja', overs: 4, maidens: 0, runs: 28, wickets: 2, economy: 7.0 },
          { name: 'Shivam Dube', overs: 4, maidens: 0, runs: 35, wickets: 1, economy: 8.75 },
          { name: 'Matheesha Pathirana', overs: 4, maidens: 0, runs: 42, wickets: 2, economy: 10.5 }
        ],
        opponentBowling: [
          { name: 'Jasprit Bumrah', overs: 4, maidens: 0, runs: 38, wickets: 2, economy: 9.5 },
          { name: 'Rahul Chahar', overs: 4, maidens: 0, runs: 32, wickets: 1, economy: 8.0 }
        ],
        cskExtras: { wides: 8, noBalls: 2, legByes: 3, byes: 1, total: 14 },
        opponentExtras: { wides: 5, noBalls: 1, legByes: 2, byes: 0, total: 8 }
      },
      {
        _id: '2',
        matchNumber: 2,
        opponent: 'Royal Challengers Bengaluru',
        venue: 'M. Chinnaswamy Stadium',
        date: '2025-03-25',
        status: 'Completed',
        scores: {
          csk: { runs: 195, wickets: 5, overs: 20 },
          opponent: { runs: 188, wickets: 7, overs: 20 }
        },
        result: { winner: 'CSK', summary: 'CSK won by 7 runs' },
        playerPerformance: {
          runs: 62,
          balls: 38,
          fours: 6,
          sixes: 3,
          wickets: 0,
          economy: 0
        }
      },
      {
        _id: '3',
        matchNumber: 3,
        opponent: 'Kolkata Knight Riders',
        venue: 'Eden Gardens',
        date: '2025-03-28',
        status: 'Completed',
        scores: {
          csk: { runs: 172, wickets: 6, overs: 20 },
          opponent: { runs: 175, wickets: 4, overs: 19.2 }
        },
        result: { winner: 'KKR', summary: 'KKR won by 6 wickets' },
        playerPerformance: {
          runs: 38,
          balls: 28,
          fours: 3,
          sixes: 2,
          wickets: 0,
          economy: 0
        }
      }
    ];
  };

  useEffect(() => {
    fetchPlayer();
  }, [id]);


  const getRoleColor = (role) => {
    const colors = {
      'Batsman': 'bg-blue-500',
      'Bowler': 'bg-green-500',
      'All-Rounder': 'bg-purple-500',
      'Wicket-Keeper': 'bg-orange-500'
    };
    return colors[role] || 'bg-gray-500';
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  if (!player) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-2xl text-gray-400">Player not found</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen pt-28 pb-12 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Back Button */}
        <motion.button
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          onClick={() => navigate('/squad')}
          className="flex items-center gap-2 text-csk-yellow hover:text-csk-yellow/80 mb-8 transition-colors"
        >
          <ArrowLeft size={20} />
          <span className="font-semibold">Back to Squad</span>
        </motion.button>

        {/* Player Header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="glass-effect rounded-xl p-8 mb-8"
        >
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {/* Player Image */}
            <div className="relative">
              <div className="aspect-square bg-gradient-to-br from-csk-yellow/20 to-csk-blue/20 rounded-lg flex items-center justify-center overflow-hidden border-2 border-csk-yellow/30">
                {player.photo ? (
                  <img
                    src={player.photo}
                    alt={player.name}
                    className="w-full h-full object-cover"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.style.display = 'none';
                      e.target.parentElement.innerHTML += '<div class="text-9xl">ðŸ‘¤</div>';
                    }}
                  />
                ) : (
                  <div className="text-9xl">ðŸ‘¤</div>
                )}
                <div className={`absolute top-3 left-3 ${getRoleColor(player.role)} px-3 py-1 rounded-full text-xs font-bold`}>
                  {player.role}
                </div>
                <div className="absolute top-4 right-4 bg-black/80 w-16 h-16 rounded-full flex items-center justify-center font-bold text-2xl border-2 border-csk-yellow">
                  {player.jerseyNumber}
                </div>
              </div>
            </div>

            {/* Player Info */}
            <div className="md:col-span-2 space-y-6">
              <div className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-6 border border-csk-yellow/30">
                <h2 className="text-3xl font-bold text-white mb-6 flex items-center gap-3">
                  <Users className="text-csk-yellow" size={24} />
                  Player Information
                </h2>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                  <div>
                    <p className="text-gray-400 text-sm mb-2">Full Name</p>
                    <p className="text-white font-bold text-lg">{player.name}</p>
                  </div>
                  <div>
                    <p className="text-gray-400 text-sm mb-2">Role</p>
                    <div className={`inline-block ${getRoleColor(player.role)} px-3 py-1 rounded-full text-sm font-bold`}>
                      {player.role}
                    </div>
                  </div>
                  <div>
                    <p className="text-gray-400 text-sm mb-2">Nationality</p>
                    <p className="text-white font-bold">{player.nationality}</p>
                  </div>
                  <div>
                    <p className="text-gray-400 text-sm mb-2">Jersey Number</p>
                    <p className="text-white font-bold text-2xl">#{player.jerseyNumber}</p>
                  </div>
                </div>

                <div className="mb-6">
                  <p className="text-gray-400 text-sm mb-2">Biography</p>
                  <p className="text-gray-300 leading-relaxed">{player.bio}</p>
                </div>

                <div className="grid grid-cols-2 gap-4 mb-6">
                  <div>
                    <p className="text-gray-400 text-sm mb-2">Batting Style</p>
                    <p className="text-white font-bold">{player.battingStyle}</p>
                  </div>
                  <div>
                    <p className="text-gray-400 text-sm mb-2">Bowling Style</p>
                    <p className="text-white font-bold">{player.bowlingStyle}</p>
                  </div>
                </div>
              </div>

              {/* Career Statistics */}
              <div className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-6 border border-csk-yellow/30">
                <h2 className="text-3xl font-bold text-white mb-6 flex items-center gap-3">
                  <TrendingUp className="text-csk-yellow" size={24} />
                  Career Statistics
                </h2>

                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
                  <div className="text-center">
                    <div className="text-3xl font-black text-csk-yellow">{player.stats?.matches || 0}</div>
                    <div className="text-xs text-gray-500 font-semibold">MATCHES</div>
                  </div>
                  <div className="text-center">
                    <div className="text-3xl font-black text-csk-yellow">{player.stats?.runs || 0}</div>
                    <div className="text-xs text-gray-500 font-semibold">RUNS</div>
                  </div>
                  <div className="text-center">
                    <div className="text-3xl font-black text-csk-yellow">{player.stats?.average || 0}</div>
                    <div className="text-xs text-gray-500 font-semibold">AVERAGE</div>
                  </div>
                  <div className="text-center">
                    <div className="text-3xl font-black text-csk-yellow">{player.stats?.strikeRate || 0}</div>
                    <div className="text-xs text-gray-500 font-semibold">STRIKE RATE</div>
                  </div>
                </div>

                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                  <div className="text-center">
                    <div className="text-3xl font-black text-csk-yellow">{player.stats?.highestScore || 0}</div>
                    <div className="text-xs text-gray-500 font-semibold">HIGHEST SCORE</div>
                  </div>
                  <div className="text-center">
                    <div className="text-3xl font-black text-csk-yellow">{player.stats?.fifties || 0}</div>
                    <div className="text-xs text-gray-500 font-semibold">FIFTIES</div>
                  </div>
                  <div className="text-center">
                    <div className="text-3xl font-black text-csk-yellow">{player.stats?.hundreds || 0}</div>
                    <div className="text-xs text-gray-500 font-semibold">HUNDREDS</div>
                  </div>
                  <div className="text-center">
                    <div className="text-3xl font-black text-csk-yellow">{player.stats?.sixes || 0}</div>
                    <div className="text-xs text-gray-500 font-semibold">SIXES</div>
                  </div>
                </div>

                {player.role === 'Bowler' && (
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    <div className="text-center">
                      <div className="text-3xl font-black text-csk-yellow">{player.stats?.wickets || 0}</div>
                      <div className="text-xs text-gray-500 font-semibold">WICKETS</div>
                    </div>
                    <div className="text-center">
                      <div className="text-3xl font-black text-csk-yellow">{player.stats?.economy || 0}</div>
                      <div className="text-xs text-gray-500 font-semibold">ECONOMY</div>
                    </div>
                    <div className="text-center">
                      <div className="text-3xl font-black text-csk-yellow">{player.stats?.bestBowling || 'N/A'}</div>
                      <div className="text-xs text-gray-500 font-semibold">BEST BOWLING</div>
                    </div>
                  </div>
                )}
              </div>

              {/* IPL 2025 Performance */}
              {player.ipl2025Contribution && (
                <div className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-6 border border-csk-yellow/30">
                  <h2 className="text-3xl font-bold text-white mb-6 flex items-center gap-3">
                    <Zap className="text-csk-yellow" size={24} />
                    IPL 2025 Performance
                  </h2>

                  <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                    <div className="text-center">
                      <div className="text-3xl font-black text-csk-yellow">{player.ipl2025Contribution.matches}</div>
                      <div className="text-xs text-gray-500 font-semibold">MATCHES</div>
                    </div>
                    <div className="text-center">
                      <div className="text-3xl font-black text-csk-yellow">{player.ipl2025Contribution.runs}</div>
                      <div className="text-xs text-gray-500 font-semibold">RUNS</div>
                    </div>
                    <div className="text-center">
                      <div className="text-3xl font-black text-csk-yellow">{player.ipl2025Contribution.average}</div>
                      <div className="text-xs text-gray-500 font-semibold">AVERAGE</div>
                    </div>
                    <div className="text-center">
                      <div className="text-3xl font-black text-csk-yellow">{player.ipl2025Contribution.impactRating}</div>
                      <div className="text-xs text-gray-500 font-semibold">IMPACT RATING</div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </motion.div>

        {/* IPL 2025 Contribution */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.1 }}
          className="glass-effect rounded-xl p-8 mb-8"
        >
          <div className="flex items-center gap-3 mb-6">
            <Trophy className="text-csk-yellow" size={28} />
            <h2 className="text-3xl font-bold">IPL 2025 Contribution</h2>
            <Star className="text-csk-yellow animate-pulse" size={24} />
          </div>
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-7 gap-4">
            <StatCard label="Matches" value={player.ipl2025Contribution.matches} />
            <StatCard label="Runs" value={player.ipl2025Contribution.runs} />
            <StatCard label="Wickets" value={player.ipl2025Contribution.wickets} />
            <StatCard label="Strike Rate" value={player.ipl2025Contribution.strikeRate.toFixed(2)} />
            <StatCard label="Average" value={player.ipl2025Contribution.average.toFixed(2)} />
            <StatCard label="Economy" value={player.ipl2025Contribution.economy.toFixed(2)} />
            <StatCard label="Impact" value={player.ipl2025Contribution.impactRating.toFixed(1)} highlight />
          </div>
        </motion.div>

        {/* Career Stats */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="glass-effect rounded-xl p-8 mb-8"
        >
          <div className="flex items-center gap-3 mb-6">
            <Target className="text-csk-yellow" size={28} />
            <h2 className="text-3xl font-bold">Career Statistics</h2>
          </div>

          {/* Batting Stats */}
          <div className="mb-8">
            <h3 className="text-xl font-bold text-csk-yellow mb-4">Batting</h3>
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
              <StatCard label="Matches" value={player.stats?.matches || 0} />
              <StatCard label="Runs" value={player.stats?.runs || 0} />
              <StatCard label="Highest Score" value={player.stats?.highestScore || 0} />
              <StatCard label="Average" value={player.stats?.average?.toFixed(2) || '0.00'} />
              <StatCard label="Strike Rate" value={player.stats?.strikeRate?.toFixed(2) || '0.00'} />
              <StatCard label="Fifties" value={player.stats?.fifties || 0} />
              <StatCard label="Hundreds" value={player.stats?.hundreds || 0} />
              <StatCard label="Fours" value={player.stats?.fours || 0} />
              <StatCard label="Sixes" value={player.stats?.sixes || 0} />
            </div>
          </div>

          {/* Bowling Stats */}
          {(player.stats?.wickets > 0) && (
            <div className="mb-8">
              <h3 className="text-xl font-bold text-csk-yellow mb-4">Bowling</h3>
              <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                <StatCard label="Wickets" value={player.stats?.wickets || 0} />
                <StatCard label="Best Bowling" value={player.stats?.bestBowling || 'N/A'} />
                <StatCard label="Economy" value={player.stats?.economy?.toFixed(2) || '0.00'} />
                <StatCard label="5W Hauls" value={player.stats?.fiveWicketHauls || 0} />
              </div>
            </div>
          )}

          {/* Fielding Stats */}
          {((player.stats?.catches > 0) || (player.stats?.stumpings > 0)) && (
            <div>
              <h3 className="text-xl font-bold text-csk-yellow mb-4">Fielding</h3>
              <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                <StatCard label="Catches" value={player.stats?.catches || 0} />
                {(player.stats?.stumpings > 0) && (
                  <StatCard label="Stumpings" value={player.stats?.stumpings || 0} />
                )}
              </div>
            </div>
          )}
        </motion.div>

        {/* Recent Matches with Scorecards */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3 }}
          className="glass-effect rounded-xl p-8 mb-8"
        >
          <div className="flex items-center gap-3 mb-6">
            <Calendar className="text-csk-yellow" size={28} />
            <h2 className="text-3xl font-bold">Recent Matches</h2>
          </div>
          <div className="space-y-4">
            {getRecentMatches().map((match, index) => (
              <motion.div
                key={match._id}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.4 + index * 0.1 }}
                className="bg-gray-800/50 border border-csk-yellow/20 rounded-lg p-5 hover:border-csk-yellow/40 transition-all duration-300"
              >
                <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-3">
                      <h3 className="text-xl font-bold text-white">
                        CSK vs {match.opponent}
                      </h3>
                      <span className={`px-2 py-1 rounded text-xs font-bold ${match.result.winner === 'CSK' ? 'bg-green-500/20 text-green-400' : 'bg-red-500/20 text-red-400'
                        }`}>
                        {match.result.winner === 'CSK' ? 'WON' : 'LOST'}
                      </span>
                    </div>

                    <div className="grid grid-cols-2 gap-4 mb-3">
                      <div className="bg-gray-800/50 rounded p-3">
                        <div className="text-xs text-gray-400 mb-1">CSK</div>
                        <div className="text-lg font-bold text-csk-yellow">
                          {match.scores.csk.runs}/{match.scores.csk.wickets}
                          <span className="text-sm text-gray-400 ml-1">({match.scores.csk.overs})</span>
                        </div>
                      </div>
                      <div className="bg-gray-800/50 rounded p-3">
                        <div className="text-xs text-gray-400 mb-1">{match.opponent}</div>
                        <div className="text-lg font-bold text-gray-300">
                          {match.scores.opponent.runs}/{match.scores.opponent.wickets}
                          <span className="text-sm text-gray-400 ml-1">({match.scores.opponent.overs})</span>
                        </div>
                      </div>
                    </div>

                    {/* Player Performance */}
                    <div className="bg-csk-yellow/10 border border-csk-yellow/30 rounded p-3">
                      <div className="text-xs text-csk-yellow font-semibold mb-2">Player Performance</div>
                      <div className="flex flex-wrap gap-4 text-sm">
                        {match.playerPerformance.runs > 0 && (
                          <div>
                            <span className="text-gray-400">Runs: </span>
                            <span className="text-white font-bold">{match.playerPerformance.runs} ({match.playerPerformance.balls})</span>
                          </div>
                        )}
                        {match.playerPerformance.fours > 0 && (
                          <div>
                            <span className="text-gray-400">Fours: </span>
                            <span className="text-white font-bold">{match.playerPerformance.fours}</span>
                          </div>
                        )}
                      </div>
                    </div>
                    <button
                      onClick={() => openScorecard(match)}
                      className="btn-primary flex items-center gap-2 whitespace-nowrap self-start lg:self-center"
                    >
                      <FileText size={18} />
                      View Scorecard
                    </button>
                </div>
              </div>
            </motion.div>
          ))}
          </div>
        </motion.div>

        {/* Career Highlights */}
        {player.careerHighlights && player.careerHighlights.length > 0 && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.6 }}
            className="glass-effect rounded-xl p-8"
          >
            <div className="flex items-center gap-3 mb-6">
              <Award className="text-csk-yellow" size={28} />
              <h2 className="text-3xl font-bold">Career Highlights</h2>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {player.careerHighlights.map((highlight, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, scale: 0.95 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ delay: 0.7 + index * 0.1 }}
                  className="card hover-glow"
                >
                  <div className="aspect-video bg-gradient-to-br from-csk-yellow/20 to-csk-blue/20 rounded-lg mb-4 flex items-center justify-center">
                    <Award size={48} className="text-csk-yellow" />
                  </div>
                  <div className="p-4">
                    <h3 className="text-xl font-bold mb-2 text-white">{highlight.title}</h3>
                    <p className="text-gray-400 mb-3 leading-relaxed">{highlight.description}</p>
                    <div className="flex items-center gap-2">
                      <span className="px-3 py-1 bg-csk-yellow/20 border border-csk-yellow rounded-full text-csk-yellow font-bold text-sm">
                        {highlight.year}
                      </span>
                    </div>
                  </div>
                </motion.div>
              ))}
            </div>
          </motion.div>
        )}
      </div>

      {/* Scorecard Modal */}
      <Scorecard
        match={selectedMatch}
        isOpen={scorecardOpen}
        onClose={closeScorecard}
      />
    </div>
  );
};

const StatCard = ({ label, value, highlight }) => (
  <div className={`text-center p-4 rounded-lg transition-all duration-300 ${highlight
    ? 'bg-gradient-to-br from-csk-yellow/30 to-csk-blue/30 border-2 border-csk-yellow shadow-lg shadow-csk-yellow/20'
    : 'bg-gray-800/50 hover:bg-gray-800/70'
    }`}>
    <div className={`text-3xl font-bold mb-1 ${highlight ? 'text-csk-yellow' : 'text-white'}`}>
      {value}
    </div>
    <div className="text-sm text-gray-400">{label}</div>
  </div>
);

export default PlayerDetail;
